this.init = ( input, engine ) => {
  this.camera = new THREE.PerspectiveCamera( 75, engine.width / engine.height, 1, 10000 )
  this.camera.position.z = 1000
}

this.process = ( input, engine ) => {
  this.camera.aspect = engine.width / engine.height
  this.camera.updateProjectionMatrix()
  return {
    Camera: this.camera
  }
}

this.flush = () => {
  delete this.camera
}

this.init = input => {
  this.material = new THREE.MeshBasicMaterial( { 
    	color: 0xffffff, 
    	wireframe: true,
      wireframeLinewidth: 2
  	}
  )
}

this.process = input => {
  return {
    Material: this.material
  }
}

this.flush = () => {
  this.material.dispose()
  delete this.material
}

this.init = input => {
  this.geometry = new THREE.IcosahedronGeometry(
    600,
    1
  )
}

this.process = input => {
  return {
    Geometry: this.geometry
  }
}

this.flush = () => {
  this.geometry.dispose()
	delete this.geometry
}

this.init = () => {
	this.clock = new THREE.Clock()
}

this.process = () => {
  let dt = this.clock.getDelta()
  return {
    X: 0.1 * dt,
    Y: 0.5 * dt,
    Z: 1.0 * dt
  }
}

this.flush = () => {

}

this.init = input => {
  this.mesh = new THREE.Mesh(
    input.Geometry,
    input.Material
  )
}

this.process = input => {
  this.mesh.rotation.x += input.Data * 0.2
  this.mesh.rotation.y += input.Data * 0.5
  this.mesh.rotation.z += input.Data
  return {
    Mesh: this.mesh
  }
}

this.flush = () => {
  delete this.mesh
  this.flushOutput()
}

this.init = input => {
  this.scene = new THREE.Scene()
  this.mesh = input.Object3D
  this.scene.add( this.mesh )
}

this.process = input => {
  return {
    Scene: this.scene
  }
}

this.flush = () => {
  this.scene.children.forEach( child => {
    this.scene.remove( child )
  } )
	delete this.scene
}

this.init = ( input, engine ) => {
  let renderer = engine.renderer
  renderer.setClearColor( 0x000000, 0 )
  renderer.clear()
}

this.process = ( input, engine ) => {
  let renderer = engine.renderer
  renderer.render( input.Scene, input.Camera )
}

this.flush = () => {

}
