this.init = ( input ) => {
  if ( this.__initialized ) return
  this.__initialized = true
  this.analyzer = input.Context.createAnalyser()
  this.analyzer.fftSize = 2048
	this.freqArray = new Uint8Array( this.analyzer.frequencyBinCount )
  console.log( 'freqBin', this.analyzer.frequencyBinCount )
}

this.process = ( input ) => {
  input.Node && input.Node.connect( this.analyzer )
  this.analyzer.getByteFrequencyData( this.freqArray )
  let thres = computeAverage( this.freqArray, 0, 1024 ) / 256
	return {
		Freq: thres
	}
}

this.flush = () => {
	if ( this.audio ) {
		this.audio.pause()
		this.audio.currentTime = 0
	}
}

function computeAverage( freqArray, start, end ) {
	return freqArray.slice( start, end ).reduce( ( a, b ) => a + b ) / ( end - start )
}

